import bpy

#____ ____ ___ ___ _ _  _ ____ ____ 
#[__  |___  |   |  | |\ | | __ [__  
#___] |___  |   |  | | \| |__] ___] 
                                   
#OPENING | https://www.youtube.com/watch?v=qmk3Rri0jsQ&ab_channel=SECRETGUEST

# Parameters

auto_smooth_angle = 30.0  # Auto Smooth angle in degrees
subdiv_levels = 2  # Subdivision levels
use_auto_smooth = True  # Toggle Auto Smooth
use_subsurf_mod = True  # Toggle Subdivision Surface modifier

#___  ____ _ _ _ ____ ____    ___  _    ____ _  _ ___
#|__] |  | | | | |___ |__/    |__] |    |__| |\ |  |
#|    |__| |_|_| |___ |  \    |    |___ |  | | \|  |
                


# Iterate through all selected objects in the scene
for obj in bpy.context.selected_objects:
    # Check if the object is a mesh
    if obj.type == 'MESH':
        
        # Apply Auto Smooth if toggled
        if use_auto_smooth:
            # Set the active object to the current object
            bpy.context.view_layer.objects.active = obj
            # Enable Shade Smooth
            bpy.ops.object.shade_smooth()
            # Enable Auto Smooth and set the angle
            obj.data.use_auto_smooth = True
            obj.data.auto_smooth_angle = auto_smooth_angle * (3.14159 / 180)  # Convert to radians
        
        # Apply Subdivision Surface modifier if toggled
        if use_subsurf_mod:
            # Create a Subdivision Surface modifier
            subdiv_mod = obj.modifiers.new(name="Subsurf", type='SUBSURF')
            # Set the subdivision levels
            subdiv_mod.levels = subdiv_levels
            subdiv_mod.render_levels = subdiv_levels

# Update the scene
bpy.context.view_layer.update()
