#You don't have to import with pip, just paste the code in blender               
import bpy
import random



#___  ____ _ _ _ ____ ____    ___  _    ____ _  _ ___
#|__] |  | | | | |___ |__/    |__] |    |__| |\ |  |
#|    |__| |_|_| |___ |  \    |    |___ |  | | \|  |
                

#OPENING | https://www.youtube.com/watch?v=_85LaeTCtV8 :3
                

#Function to curve each cables
def create_cable(points, thickness, index):
    
    curve_data = bpy.data.curves.new(f'cable_curve_{index}', 'CURVE')
    curve_data.dimensions = '3D'
    curve_data.resolution_u = 2
    curve_data.bevel_depth = thickness
    curve_data.bevel_resolution = 3


    #Adds a Bézier spline to the curve
    spline = curve_data.splines.new('BEZIER')
    spline.bezier_points.add(len(points) - 1)

    #Defines the control points of the spline
    for i, point in enumerate(points):
        spline.bezier_points[i].co = point
        spline.bezier_points[i].handle_left_type = 'AUTO'
        spline.bezier_points[i].handle_right_type = 'AUTO'

    #Create a new cable object from the curve
    obj_cable = bpy.data.objects.new(f'Cable_{index}', curve_data)
    bpy.context.collection.objects.link(obj_cable)
    return obj_cable





#Function to generate cables
def p_cables_generator(p_start, p_end, p_num, noises):
    points = [p_start]
    for i in range(1, p_num - 1):
        
        # x = Interpolation between start and end points based on the index (i) and the total number of points (so the relative position of point along the line).
        # then generate a random value to get variation o the curve with an organic look
        x = p_start[0] + (p_end[0] - p_start[0]) * i / (p_num - 1) + random.uniform(-noises, noises)
        y = p_start[1] + (p_end[1] - p_start[1]) * i / (p_num - 1) + random.uniform(-noises, noises)
        z = p_start[2] + (p_end[2] - p_start[2]) * i / (p_num - 1) + random.uniform(-noises, noises)
        points.append((x, y, z))

    points.append(p_end)
    return points





#Settings to modify
p_start = (0, 0, 0)  #Starting point of cables (x, y, z)
p_end = (0, 0, 2)    #Endpoint (x, y, z)
thickness = 0.02     #Cable thickness
num_cables = 20      #Number
p_num = 10           #Number of control points for each cable
noises = 0.05        #Noise factor for the variation of the position of the points (bigger = more variation)



for i in range(num_cables):
    points = p_cables_generator(p_start, p_end, p_num, noises)
    obj_cable = create_cable(points, thickness, i)
