#You don't have to import with pip, just paste the code in blender     
       
import bpy
import math


#____ ____ ___ ___ _ _  _ ____ ____ 
#[__  |___  |   |  | |\ | | __ [__  
#___] |___  |   |  | | \| |__] ___] 
                                   

 # Here you can change the value if you need 
  

circle_radius = 142.5       #The radius of the circle
circle_height = 108         #The height of the circle
animation_frames = 60       #Numer of frames the animation will have


#After that you can modify the circle with "s" in blender or its position and the position of the plain axe who is 
#the center of the view.



#___  ____ _ _ _ ____ ____    ___  _    ____ _  _ ___
#|__] |  | | | | |___ |__/    |__] |    |__| |\ |  |
#|    |__| |_|_| |___ |  \    |    |___ |  | | \|  |
               
#OPENING | https://www.youtube.com/watch?v=_85LaeTCtV8 :3

# Create a circle
bpy.ops.mesh.primitive_circle_add(vertices=64, radius=circle_radius, location=(0, 0, circle_height))
circle = bpy.context.active_object

# Add a Plain Axes object at the world origin
bpy.ops.object.empty_add(type='PLAIN_AXES', location=(0, 0, 0))
plain_axes = bpy.context.active_object

# Add a camera
bpy.ops.object.camera_add(location=(circle_radius, 0, circle_height))
camera = bpy.context.active_object

# Set the camera to point at the Plain Axes object
bpy.context.view_layer.objects.active = camera
bpy.ops.object.constraint_add(type='TRACK_TO')
track_to_constraint = camera.constraints[-1]
track_to_constraint.target = plain_axes
track_to_constraint.track_axis = 'TRACK_NEGATIVE_Z'
track_to_constraint.up_axis = 'UP_Y'

# Set up the animation
bpy.context.scene.frame_start = 1
bpy.context.scene.frame_end = animation_frames

# Create a circle path for the camera to follow
bpy.ops.curve.primitive_bezier_circle_add(radius=circle_radius, location=(0, 0, circle_height))
circle_path = bpy.context.active_object

# Parent the camera to the circle path
camera.select_set(True)
circle_path.select_set(True)
bpy.context.view_layer.objects.active = circle_path
bpy.ops.object.parent_set(type='FOLLOW')

# Set up the animation duration
bpy.context.scene.frame_start = 1
bpy.context.scene.frame_end = animation_frames
bpy.ops.anim.keyframe_insert_menu(type='Location', confirm_success=True)

# Calculate the angle increment
angle_increment = 2 * math.pi / animation_frames

for i in range(1, animation_frames + 1):
    angle = angle_increment * i
    x = circle_radius * math.cos(angle)
    y = circle_radius * math.sin(angle)
    
    camera.location = (x, y, circle_height)
    bpy.context.scene.frame_set(i)
    bpy.ops.anim.keyframe_insert_menu(type='Location', confirm_success=True)

