#You don't have to import with pip, just paste the code in blender     
          
import bpy
import bmesh
import math
import random
from mathutils import Vector
from mathutils.bvhtree import BVHTree

#___  ____ _ _ _ ____ ____    ___  _    ____ _  _ ___
#|__] |  | | | | |___ |__/    |__] |    |__| |\ |  |
#|    |__| |_|_| |___ |  \    |    |___ |  | | \|  |
                

#OPENING | https://www.youtube.com/watch?v=_85LaeTCtV8 :3


#Function gets the BVH(Bounding Volume Hierarchy) tree for the object given
def get_character_bvh_tree(character):

    depsgraph = bpy.context.evaluated_depsgraph_get() #Get the dependency graph
    bm = bmesh.new() #Create a new BMesh
    bm.from_object(character, depsgraph) #Fill the BMesh with the evaluated mesh data of the character object
    bm.transform(character.matrix_world) #Transform the BMesh to match the character's world matrix
    
    return BVHTree.FromBMesh(bm) #Returns BVH tree from the BMesh


#Function creates a new copy of a mech part object from a template, sets its location and rotation, and adds it to the scene
def make_part(part_name, location, rotation):

    part_template = bpy.data.objects[part_name] #retrieves the object using  its name
    part = part_template.copy() #Create a copy of the template object

    part.data = part_template.data.copy() #Create a copy of the template mesh data
    part.location  = location #Set the location for the new part
    part.rotation_euler = rotation #Set its rotation

    bpy.context.scene.collection.objects.link(part) #Link the new part to the scene collection

    return part #Return the new object



#Function to generate a random point on the surface of the character object,finds the nearest surface point using the BVH tree, then returns it
def get_surface_point(character, bvh_tree):
    
    bbox_center_character = character.matrix_world @ Vector(character.bound_box[0])+ character.location #Calculate the center of the character's bounding box

    while True:

        random_point  = Vector([random.uniform(-1, 1), random.uniform(-1, 1),random.uniform(-1, 1)]) #Generate a random point in 3D space
        random_point = bbox_center_character + random_point * character.dimensions #Scale and shift the point to fit within the character's bounding box
        nearest_point, _, _, _ = bvh_tree.find_nearest(random_point) #Find the nearest surface point using BVH tree

        if nearest_point is not None: #If a nearest point is found, return it
            return nearest_point


#Function to generate a number of mech parts at random surface locations on the character object and optionally adds mirrored copies of the parts
def gen_objects(character, part_names, num_parts, use_symmetry):

    character_bvh_tree = get_character_bvh_tree(character) #Get the BVH tree for the character object
    mech_parts = []

    for _ in range(num_parts):

        location = get_surface_point(character, character_bvh_tree) #Get a random surface point on the character object
        rotation = Vector([random.uniform(0, math.pi) for _ in range(3)]) #Generate a random rotation in radians
        part_name = random.choice(part_names) #Choose a part name at random from the provided list
        part = make_part(part_name, location, rotation) #Create a new mech part object at the surface point with the random rotation

        mech_parts.append(part) #Add the new part to the list of mech parts


        #If the use_symmetry flag is True, create mirrored copies of the mech parts
        if use_symmetry:
            
            mirrored_location = location * Vector([-1, 1, 1]) #Mirror the location across the x-axis
            mirrored_rotation = Vector([rotation[0], -rotation[1], rotation[2]]) #Reverse the sign of the y-axis rotation to mirror the part
            mirrored_part = make_part(part_name, mirrored_location, mirrored_rotation) #Create a new mirrored mech part object

            mech_parts.append(mirrored_part) #Add the new mirrored part to the list of mech parts

        return mech_parts #Return the list of mech parts, including any mirrored copies created if use_symmetry was True


character = bpy.data.objects['f_an01']                                          #Name the character
part_names = ('Cable_0', 'Cable_0', 'Cable_0')                                  #Objects to duplicate on the character
num_parts = 10                                                                  #Number of parts
use_symmetry = True                                                             #Use Symmetry

mech_parts = gen_objects(character, part_names, num_parts, use_symmetry)        

